    workflow.setVariable("VAR_AUTO_LOCATION", install_path + "\\Automation");
    workflow.setVariable("VAR_CVAUTOMATION", install_path + "\\Automation\\CVAutomation.py");
    logger.info("VAR_INSTALL_PATH = ["+install_path+"]");
    logger.info("VAR_AUTO_LOCATION = ["+workflow.getVariable("VAR_AUTO_LOCATION")+"]");
    logger.info("VAR_CVAUTOMATION = ["+workflow.getVariable("VAR_CVAUTOMATION")+"]");
    
    String _pass = xpath:{/workflow/inputs/INP_NetworkPassword}.toString();
    _pass = utils.decryptString(_pass);
    workflow.setVariable("VAR_NETWORK_PASSWORD", _pass);
}
catch(e)
{
    logger.info("EXCEPTION: ["+e+"]");
    workflow.setVariable("VAR_WORKFLOW_MESSAGE","Failed to set install path. Exception: " + e);
    workflow.setVariable("VAR_WORKFLOW_STATUS","FAILED");
}

