onEnter.script:
workflow.setVariable("VAR_WORKFLOW_PHASE","PHASE START : [" + xpath:{/workflow/system/jobId} + "][" + xpath:{/workflow/system/workflow/workflowName} + "][" + xpath:{/workflow/system/startTime} +"] : START WORKFLOW ");
logger.info("INFO: VAR_WORKFLOW_PHASE : [" + workflow.getVariable("VAR_WORKFLOW_PHASE") + "]");

import java.util.Calendar;
import java.text.SimpleDateFormat;
String date = new SimpleDateFormat("MM-DD-YYYY_HH_mm_ss").format(new Date());
String newDate = date.trim().replaceAll("\\s", "");
workflow.setVariable("VAR_DATE_STR", newDate);
logger.info("Setting VAR_DATE_STR: [" + workflow.getVariable("VAR_DATE_STR") + "]");

if (xpath:{/workflow/inputs/INP_LIBRARY_NAME} == "" || xpath:{/workflow/inputs/INP_LIBRARY_NAME} == null)
{
    String str = "WF_LIB_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_LIBRARY_NAME",str);
}
else
{
    workflow.setVariable("VAR_INP_LIBRARY_NAME",xpath:{/workflow/inputs/INP_LIBRARY_NAME});    
}

if (xpath:{/workflow/inputs/INP_MOUNT_PATH} == "" || xpath:{/workflow/inputs/INP_MOUNT_PATH} == null)
{
    String str = "C:\\WORKFLOW\\WF_LIB_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_MOUNT_PATH",str);
}
else
{
    workflow.setVariable("VAR_INP_MOUNT_PATH",xpath:{/workflow/inputs/INP_MOUNT_PATH});
}

logger.info("Setting VAR_INP_LIBRARY_NAME: [" + workflow.getVariable("VAR_INP_LIBRARY_NAME") + "]");
logger.info("Setting VAR_INP_MOUNT_PATH: [" + workflow.getVariable("VAR_INP_MOUNT_PATH") + "]");

