onEnter.script:
workflow.setVariable("VAR_WORKFLOW_PHASE","PHASE START : [" + xpath:{/workflow/system/jobId} + "][" + xpath:{/workflow/system/workflow/workflowName} + "][" + xpath:{/workflow/system/startTime} +"] : START WORKFLOW ");
logger.info("INFO: VAR_WORKFLOW_PHASE : [" + workflow.getVariable("VAR_WORKFLOW_PHASE") + "]");

import java.util.Calendar;
import java.text.SimpleDateFormat;
String date = new SimpleDateFormat("MM-DD-YYYY_HH_mm_ss").format(new Date());
String newDate = date.trim().replaceAll("\\s", "");
workflow.setVariable("VAR_DATE_STR", newDate);
logger.info("INFO: VAR_DATE_STR: [" + workflow.getVariable("VAR_DATE_STR") + "]");

String temp = "C:\\WORKFLOW" + "\\" + xpath:{/workflow/system/jobId} + "_" + workflow.getVariable("VAR_DATE_STR");
workflow.setVariable("VAR_DEST_PATH",temp);
logger.info("INFO: VAR_DEST_PATH: [" + workflow.getVariable("VAR_DEST_PATH") + "]");

String temp = workflow.getVariable("VAR_DEST_PATH") + "\\filters.txt";
workflow.setVariable("VAR_FILE_PATH",temp);
logger.info("INFO: VAR_FILE_PATH: [" + workflow.getVariable("VAR_FILE_PATH") + "]");

if (xpath:{/workflow/inputs/INP_SCHPOL_NAME} == "" || xpath:{/workflow/inputs/INP_SCHPOL_NAME} == null)
{
    String str = "WF_SCHPOL_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_SCHPOL_NAME",str);
}
else
{   
    workflow.setVariable("VAR_INP_SCHPOL_NAME",xpath:{/workflow/inputs/INP_SCHPOL_NAME});
}

if (xpath:{/workflow/inputs/INP_SCHPOL_SCHEDULE_NAME} == "" || xpath:{/workflow/inputs/INP_SCHPOL_SCHEDULE_NAME} == null)
{
    String str = "WF_SCHPOL_SCH_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_SCHPOL_SCHEDULE_NAME",str);
}
else
{   
    workflow.setVariable("VAR_INP_SCHPOL_SCHEDULE_NAME",xpath:{/workflow/inputs/INP_SCHPOL_SCHEDULE_NAME});
}

logger.info("Setting VAR_INP_SCHPOL_NAME: [" + workflow.getVariable("VAR_INP_SCHPOL_NAME") + "]");
logger.info("Setting VAR_INP_SCHPOL_SCHEDULE_NAME: [" + workflow.getVariable("VAR_INP_SCHPOL_SCHEDULE_NAME") + "]");

