onEnter.script:
workflow.setVariable("VAR_WORKFLOW_PHASE","PHASE START : [" + xpath:{/workflow/system/jobId} + "][" + xpath:{/workflow/system/workflow/workflowName} + "][" + xpath:{/workflow/system/startTime} +"] : START WORKFLOW ");
logger.info("INFO: VAR_WORKFLOW_PHASE : [" + workflow.getVariable("VAR_WORKFLOW_PHASE") + "]");

import java.util.Calendar;
import java.text.SimpleDateFormat;
String date = new SimpleDateFormat("MM-DD-YYYY_HH_mm_ss").format(new Date());
String newDate = date.trim().replaceAll("\\s", "");
workflow.setVariable("VAR_DATE_STR", newDate);
logger.info("Setting VAR_DATE_STR: [" + workflow.getVariable("VAR_DATE_STR") + "]");

if (xpath:{/workflow/inputs/INP_COMMON_BASE_FOLDER} == "" || xpath:{/workflow/inputs/INP_COMMON_BASE_FOLDER} == null)
{
    String str = "C:\\WORKFLOW\\WF_COMMON_BASE_FOLDER_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_COMMON_BASE_FOLDER",str);
}
else
{
    workflow.setVariable("VAR_INP_COMMON_BASE_FOLDER",xpath:{/workflow/inputs/INP_COMMON_BASE_FOLDER});
}

if (xpath:{/workflow/inputs/INP_REPLICATION_SET_NAME} == "" || xpath:{/workflow/inputs/INP_REPLICATION_SET_NAME} == null)
{
    String str = "WF_REPSET_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_REPLICATION_SET_NAME",str);
}
else
{   
    workflow.setVariable("VAR_INP_REPLICATION_SET_NAME",xpath:{/workflow/inputs/INP_REPLICATION_SET_NAME});
}

if (xpath:{/workflow/inputs/INP_SOURCE_PATH} == "" || xpath:{/workflow/inputs/INP_SOURCE_PATH} == null)
{
    String str = "C:\\WF_SOURCE_PATH_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_SOURCE_PATH",str);
}
else
{   
    workflow.setVariable("VAR_INP_SOURCE_PATH",xpath:{/workflow/inputs/INP_SOURCE_PATH});
}

logger.info("Setting VAR_INP_COMMON_BASE_FOLDER: [" + workflow.getVariable("VAR_INP_COMMON_BASE_FOLDER") + "]");
logger.info("Setting VAR_INP_REPLICATION_SET_NAME: [" + workflow.getVariable("VAR_INP_REPLICATION_SET_NAME") + "]");
logger.info("Setting VAR_INP_SOURCE_PATH: [" + workflow.getVariable("VAR_INP_SOURCE_PATH") + "]");

