import java.util.Calendar;
import java.text.SimpleDateFormat;
String date = new SimpleDateFormat("MM-DD-YYYY_HH_mm_ss").format(new Date());
String newDate = date.trim().replaceAll("\\s", "");
workflow.setVariable("VAR_DATE_STR", newDate);
logger.info("Setting VAR_DATE_STR: [" + workflow.getVariable("VAR_DATE_STR") + "]");

if (xpath:{/workflow/inputs/INP_USERNAME} == "" || xpath:{/workflow/inputs/INP_USERNAME} == null)
{
    String str = "WF_USER_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_USERNAME",str);
}
else
{
    workflow.setVariable("VAR_INP_USERNAME",xpath:{/workflow/inputs/INP_USERNAME});
}

logger.info("Setting VAR_INP_USERNAME: [" + workflow.getVariable("VAR_INP_USERNAME") + "]");

