onEnter.script:
if (xpath:{/workflow/inputs/INP_ADD_USERS_TO_GROUP} && xpath:{/workflow/inputs/INP_USER_NAME} != null && !xpath:{/workflow/inputs/INP_USER_NAME}.isEmpty())
{
    logger.info("INFO: INP_USER_NAME [" + xpath:{/workflow/inputs/INP_USER_NAME} + "]");
    logger.info("INFO: INP_ADD_USERS_TO_GROUP [" + xpath:{/workflow/inputs/INP_ADD_USERS_TO_GROUP} + "]");
    
    if (xpath:{/workflow/inputs/INP_USER_NAME} == "" || xpath:{/workflow/inputs/INP_USER_NAME} == null)
    {
            workflow.setVariable("VAR_INP_USER_NAME",xpath:{/workflow/variables/VAR_INP_USERNAME});
    }
    else
    {
        String[] arrList = xpath:{/workflow/inputs/INP_USER_NAME}.split(" ");
        workflow.setVariable("VAR_INP_USER_NAME",arrList);
    }
    logger.info("INFO : VAR_INP_USER_NAME [" + workflow.getVariable("VAR_INP_USER_NAME") + "] ");
}
