onEnter.script:
workflow.setVariable("VAR_WORKFLOW_PHASE","PHASE START : [" + xpath:{/workflow/system/jobId} + "][" + xpath:{/workflow/system/workflow/workflowName} + "][" + xpath:{/workflow/system/startTime} +"] : START WORKFLOW ");
logger.info("INFO: VAR_WORKFLOW_PHASE : [" + workflow.getVariable("VAR_WORKFLOW_PHASE") + "]");

import java.util.Calendar;
import java.text.SimpleDateFormat;
String date = new SimpleDateFormat("MM-DD-YYYY_HH_mm_ss").format(new Date());
String newDate = date.trim().replaceAll("\\s", "");
workflow.setVariable("VAR_DATE_STR", newDate);
logger.info("Setting VAR_DATE_STR: [" + workflow.getVariable("VAR_DATE_STR") + "]");

if (xpath:{/workflow/inputs/INP_DIR1} == "" || xpath:{/workflow/inputs/INP_DIR1} == null)
{
    String str = "C:\\WORKFLOW\\WF_DIR1_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_DIR1",str);
    logger.info("INFO : VAR_INP_DIR1 : [" + workflow.getVariable("VAR_INP_DIR1") + "]");
}
if (xpath:{/workflow/inputs/INP_DIR2} == "" || xpath:{/workflow/inputs/INP_DIR2} == null)
{
    String str = "C:\\WORKFLOW\\WF_DIR2_" + workflow.getVariable("VAR_DATE_STR");
    workflow.setVariable("VAR_INP_DIR2",str);
    logger.info("INFO : VAR_INP_DIR2 : [" + workflow.getVariable("VAR_INP_DIR2") + "]");
}

